library( ggplot2 );
library( scales );

plot_revigo <- function(revigo.names, revigo.data, category) {
  one.data <- data.frame(revigo.data);
  names(one.data) <- revigo.names;
  one.data <- one.data [(one.data$plot_X != "null" & one.data$plot_Y != "null"), ];
  one.data$plot_X <- as.numeric( as.character(one.data$plot_X) );
  one.data$plot_Y <- as.numeric( as.character(one.data$plot_Y) );
  one.data$log_size <- as.numeric( as.character(one.data$log_size) );
  one.data$value <- as.numeric( as.character(one.data$value) );
  one.data$frequency <- as.numeric( as.character(one.data$frequency) );
  one.data$uniqueness <- as.numeric( as.character(one.data$uniqueness) );
  one.data$dispensability <- as.numeric( as.character(one.data$dispensability) );
  p1 <- ggplot( data = one.data );
  p1 <- p1 + geom_point( aes( plot_X, plot_Y, colour = value, size = log_size), alpha = I(0.6) );
  p1 <- p1 + scale_colour_gradientn( colours = heat_hcl(7), limits = c( min(one.data$value), 0) );
  p1 <- p1 + geom_point( aes(plot_X, plot_Y, size = log_size), shape = 21, fill = "transparent", colour = I (alpha ("black", 0.6) ));
  p1 <- p1 + scale_size( range=c(5, 10)) + theme_bw();  #+ scale_fill_gradientn(colours = heat_hcl(7), limits = c(-300, 0) );
  ex <- one.data [ one.data$dispensability < 0.15, ];
  p1 <- p1 + geom_text( data = ex, aes(plot_X, plot_Y, label = description), colour = I(alpha("black", 0.85)), size = 6 );
  p1 <- p1 + theme(axis.title = element_text(size = 15)) + theme(axis.text = element_text(size = 20));
  p1 <- p1 + labs (y = "semantic space x", x = "semantic space y");
  p1 <- p1 + theme(legend.key = element_blank()) ;
  one.x_range = max(one.data$plot_X) - min(one.data$plot_X);
  one.y_range = max(one.data$plot_Y) - min(one.data$plot_Y);
  p1 <- p1 + xlim(min(one.data$plot_X)-one.x_range/10,max(one.data$plot_X)+one.x_range/10);
  p1 <- p1 + ylim(min(one.data$plot_Y)-one.y_range/10,max(one.data$plot_Y)+one.y_range/10);
  #p1;
  ggsave(paste("/Users/renaschweizer_old/Desktop/LAB_PROJECTS/post_doc_ChevironLab/rnaSeq_diaphragm_Ivy_etal_2021/analysis/REVIGO_output/revigo-plot_",category,".pdf",sep=""));
  return()
}

